SELECT Artista.nombre,Email.email FROM Artista, Banda, Miembro, Email, HasEmail WHERE Artista.id = Miembro.ida AND Banda.id = Miembro.idb AND HasEmail.id = Artista.id AND Email.email = HasEmail.email AND Miembro.fecha_ingreso < now() AND Miembro.fecha_abandono > now() AND Banda.nombre = $1;
SELECT artista.nombre, artistas_auxiliar.cuenta FROM artista, (SELECT ida, COUNT(ida) as cuenta FROM artistaautorof WHERE idd IN (SELECT disco.id FROM disco WHERE disco.fecha > $1) GROUP BY ida UNION SELECT miembro.ida, prueba.cuenta FROM miembro, (SELECT idb, COUNT(idb) as cuenta FROM bandaautorof WHERE idd IN (SELECT disco.id FROM disco WHERE disco.fecha > $1) GROUP BY idb) AS prueba WHERE prueba.idb = miembro.idb) AS artistas_auxiliar WHERE artistas_auxiliar.ida = artista.id ORDER BY artistas_auxiliar.cuenta DESC LIMIT 1;
SELECT banda.nombre FROM artista, banda, toca_en Where artista.aid = toca_en.aid AND banda.bid = toca_en.bid AND artista.nombre = $1;
Select noticiaartista.* from artista, aaparece_en, noticiaartista where artista.aid = aaparece_en.aid and noticiaartista.nid = aaparece_en.nid and artista.nombre = $1 union select noticiabandas.* from noticiabandas, banda, artista, baparece_en, toca_en where banda.bid = toca_en.bid and artista.aid = toca_en.aid and artista.nombre = $1 and noticiabandas.nid = baparece_en.nid and banda.bid = baparece_en.bid;
